package by.epam.training.domain;import java.io.Serializable;import java.util.Date;/** * Entity class {@code Reservation} is the class, that describes reservation of this system. * It contains two constructors and basic methods to deal with reservation objects. * @author Mikhail Kerko */public class Reservation implements Serializable {    private static final long serialVersionID = 1L;    private Long id;    private String userID;    private String guestNumber;    private String roomID;    private String startDate;    private String endDate;    private boolean isApproved;    public Reservation(Long id, String userID, String guestNumber, String roomID, String startDate, String endDate, boolean isApproved) {        this.setOrderID(id);        this.setUserID(userID);        this.setGuestNumber(guestNumber);        this.setRoomID(roomID);        this.setStartDate(startDate);        this.setEndDate(endDate);        this.setIsApproved(isApproved);    }    public Reservation() {    }    public Long getOrderID() {        return id;    }    public void setOrderID(Long orderID) {        this.id = orderID;    }    public String getUserID() {        return userID;    }    public void setUserID(String userID) {        this.userID = userID;    }    public String getGuestNumber() {        return guestNumber;    }    public void setGuestNumber(String guestNumber) {        this.guestNumber = guestNumber;    }    public String getRoomID() {        return roomID;    }    public void setRoomID(String roomID) {        this.roomID = roomID;    }    public String getStartDate() {        return startDate;    }    public void setStartDate(String startDate) {        this.startDate = startDate;    }    public String  getEndDate() {        return endDate;    }    public void setEndDate(String endDate) {        this.endDate = endDate;    }    public boolean getIsApproved() {        return isApproved;    }    public void setIsApproved(boolean isApproved) {        this.isApproved = isApproved;    }    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        Reservation that = (Reservation) o;        if (!roomID.equals(that.roomID)) return false;        if (!startDate.equals(that.startDate)) return false;        return endDate.equals(that.endDate);    }    @Override    public int hashCode() {        int result = roomID.hashCode();        result = 31 * result + startDate.hashCode();        result = 31 * result + endDate.hashCode();        return result;    }    @Override    public String toString() {        return "Reservation{" +                "orderID=" + id +                ", userID=" + userID +                ", guestNumber=" + guestNumber +                ", roomID=" + roomID +                ", startDate='" + startDate + '\'' +                ", endDate='" + endDate + '\'' +                '}';    }}